import React from 'react';
import './Home.css';
import { useEffect, useRef } from 'react';
const titleRef = useRef(null);

useEffect(() => {
  const observer = new IntersectionObserver(
    ([entry]) => {
      if (entry.isIntersecting) {
        titleRef.current.classList.add('animate');
      }
    },
    { threshold: 0.5 }
  );

  if (titleRef.current) {
    observer.observe(titleRef.current);
  }

  return () => {
    if (titleRef.current) observer.unobserve(titleRef.current);
  };
}, []);

const Home = () => {
  return (
    <>
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1 className="hero-title fade-in">
            <span className="highlight">
              Tackling the<br />
              problems of today, whilst creating the leaders of tomorrow.
            </span>
          </h1>
          <h3 className="hero-subtitle fade-in">
            <strong>
              JOIN US TO ENGAGE THE NEXT GENERATION OF ENTREPRENEURIAL LEADERS TO USE INNOVATION AND BUSINESS TO IMPROVE THE WORLD.
            </strong>
          </h3>
        </div>

        {/* Wave at Bottom of Hero */}
        <div className="wave-bottom">
          <svg viewBox="0 0 1440 320" preserveAspectRatio="none">
            <path
              d="M0,224L48,218.7C96,213,192,203,288,186.7C384,171,480,149,576,160C672,171,768,213,864,208C960,203,1056,149,1152,117.3C1248,85,1344,75,1392,69.3L1440,64L1440,320L0,320Z"
              fill="#ffffff"
            ></path>
          </svg>
        </div>
      </section>

      {/* Enactus Section (Under Hero) */}
      <section className="enactus-section">
        <div className="enactus-content">
          <div className="enactus-text">
          <h2 className="enactus-title">What is ENACTUS?</h2> {/* <-- Title added here */}
          <p className="enactus-paragraph">
  <strong>Enactus</strong> is a global network that inspires young people to use entrepreneurship
  to solve social and economic challenges. Enactus Maroc, founded in 2003, is part of this network
  and offers students the opportunity to carry out innovative projects with a direct impact
  on their community.
</p>

          </div>
          <div className="enactus-image">
            <img src="/img/ena.jpg" alt="Enactus" />
          </div>
        </div>
      </section>
    </>
  );
};

export default Home;
