import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  const [isSticky, setIsSticky] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`fixed top-0 left-0 w-full z-50 ${isSticky ? 'bg-white shadow-lg' : 'bg-transparent'}`}>
      <div className="max-w-screen-xl mx-auto px-4 py-2 flex items-center justify-between">
        <Link to="/" className="text-2xl font-bold text-yellow-500">
          Enactus Club
        </Link>
        <nav className="hidden md:flex space-x-6">
          <Link to="/" className="text-gray-800 hover:text-yellow-500">Accueil</Link>
          <Link to="/about" className="text-gray-800 hover:text-yellow-500">À propos</Link>
          <Link to="/projects" className="text-gray-800 hover:text-yellow-500">Projets</Link>
          <Link to="/contact" className="text-gray-800 hover:text-yellow-500">Contact</Link>
        </nav>
        <div className="md:hidden">
          {/* Icône de menu mobile */}
        </div>
      </div>
    </header>
  );
};

export default Header;
