import React,{useEffect, useRef, useState} from "react";
import '../App.css';
function Hooks(props){
    const [count,setCount]=useState(0);
    const premiereFois=useRef(true)
        function hundelclickAjout(){
            setCount(count+1)
        }
        function hundleClickReduit(){
            setCount(count-1)
        }
        useEffect(function(){
            // console.log(count +"a changé")
            if(premiereFois.current)
                premiereFois.current=false
            else
                document.getElementById("divMessage").style.color="black"
        },[count])
        return(
            <div id="divMessage">
                vous avez cliquer {count}
                <br/>
                <button onClick={hundelclickAjout}>Incrementer par 1</button>
                <button onClick={hundleClickReduit}>Decrementer par 1</button>
            </div>
        )
}
export default Hooks;
